(8, 'grid', 'category', '<div>\r\n	<div class="category_main_toolbar">\r\n		<div class="category_main_title">{category_main_name}</div>\r\n		<!-- <div>{filter_by_lbl}{filter_by}</div>-->\r\n		<div>{order_by_lbl}{order_by}</div>\r\n		<div>{template_selector_category_lbl}{template_selector_category}</div>\r\n	</div>\r\n	{if subcats}\r\n	<div>\r\n		<div>{category_loop_start}\r\n			<div id="categories">\r\n				<div style="float: left; width: 200px;">\r\n					<div class="category_image">{category_thumb_image}</div>\r\n					<div class="category_description">\r\n						<h2 class="category_title">{category_name}</h2>\r\n					</div>\r\n					<div class="category_description">{category_readmore}</div>\r\n					<div class="category_description">{category_description}</div>\r\n				</div>\r\n			</div>\r\n			{category_loop_end}\r\n		</div>\r\n	</div>\r\n	{subcats end if}\r\n	<div class="category_box_wrapper">{product_loop_start}\r\n		<div class="category_box_outside">\r\n			<div class="category_box_inside">\r\n				<table border="0">\r\n					<tbody>\r\n					<tr>\r\n						<td colspan="2">\r\n							<div class="category_product_image">{product_thumb_image}</div>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td colspan="2">\r\n							<div class="category_product_title">\r\n								<h3>{product_name}</h3>\r\n							</div>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td>\r\n							<div class="category_product_price">{product_price}</div>\r\n						</td>\r\n						<td>\r\n							<div class="category_product_addtocart">{form_addtocart:add_to_cart1}</div>\r\n						</td>\r\n					</tr>\r\n					</tbody>\r\n				</table>\r\n			</div>\r\n		</div>\r\n		{product_loop_end}\r\n	</div>\r\n	<div class="category_pagination">{pagination}</div>\r\n</div>', '', '', 0, '', 127, '2014-07-07 07:17:16'),
(9, 'product', 'product', '<div class="product">\r\n	<div class="gd_navigation"><span class="gd_nav_left">{navigation_link_left}</span><span class="gd_nav_right">{navigation_link_right}</span>\r\n	</div>\r\n\r\n	<div class="redSHOP_product_box clearfix">\r\n		<div class="redSHOP_product_box_left">\r\n			<div class="product_image">{product_thumb_image}</div>\r\n			<div class="product_more_images">{more_images}</div>\r\n			<div class="redSHOP_links">\r\n				<span>{ask_question_about_product}</span>\r\n\r\n				<div>{manufacturer_link}</div>\r\n			</div>\r\n		</div>\r\n		<div class="redSHOP_product_box_right">\r\n			<div class="redSHOP_product_detail_box">\r\n				<div class="product_title clearfix">\r\n					<h3>{product_name}</h3>\r\n				</div>\r\n				<div class="product_desc_short">{product_s_desc}</div>\r\n			</div>\r\n\r\n			<div class="addtocart_box">\r\n				<div class="addtocart_area">\r\n					<div class="cardiv1">\r\n\r\n						{attribute_template:attributes}\r\n\r\n						{accessory_template:accessory}\r\n					</div>\r\n					<div class="cardiv2">\r\n						<div class="clearfix pricebox">\r\n							<div class="stockholder">{stock_status:instock:outofstock}</div>\r\n\r\n							<div class="areacart">\r\n\r\n								<div id="product_price"><span class="product_price_val">{product_price}</span>\r\n									<span class="eks_vat">{vat_info}</span>\r\n								</div>\r\n								<div class="product_addtocart">\r\n									<div id="add_to_cart_all">{form_addtocart:add_to_cart2}</div>\r\n								</div>\r\n							</div>\r\n\r\n						</div>\r\n					</div>\r\n				</div>\r\n			</div>\r\n		</div>\r\n	</div>\r\n</div>\r\n<div>&nbsp;</div>\r\n<div class="product_box_outside">\r\n	<div style="border-bottom: 1px solid #eee;"><h4>Description</h4></div>\r\n	<div class="product_desc">\r\n		<div class="product_desc_full">{product_desc}</div>\r\n	</div>\r\n</div>\r\n\r\n<div class="gd_header"><h4>Customer Reviews</h4></div>\r\n<div class="gd_content clearfix">\r\n	<div>{product_rating_summary}</div>\r\n	<div>{form_rating}</div>\r\n</div>\r\n\r\n<div>&nbsp;</div>\r\n{related_product:related_products}', '', '', 1, '', 127, '2014-07-07 07:17:06'),
(10, 'cart', 'cart', '<h1>{cart_lbl}</h1>\n<table class="tdborder" style="width: 100%;" border="0" cellspacing="0" cellpadding="0">\n	<thead>\n	<tr>\n		<th>{product_name_lbl}</th>\n		<th><br/></th>\n		<th>{product_price_excl_lbl}</th>\n		<th>{quantity_lbl}</th>\n		<th>{total_price_exe_lbl}</th>\n	</tr>\n	</thead>\n	<tbody>\n	<!-- {product_loop_start} -->\n	<div class="category_print">{attribute_price_with_vat}</div>\n	<tr class="tdborder">\n		<td>\n			<div class="cartproducttitle">{product_name}</div>\n			<div class="cartattribut">{product_attribute}</div>\n			<div class="cartaccessory">{product_accessory}</div>\n			<div class="cartwrapper">{product_wrapper}</div>\n			<div class="cartuserfields">{product_userfields}</div>\n			<div>{attribute_change}</div>\n		</td>\n		<td>{product_thumb_image}</td>\n		<td>{product_price_excl_vat}</td>\n		<td>\n			<table border="0">\n				<tbody>\n				<tr>\n					<td>{update_cart}</td>\n					<td>{remove_product}</td>\n				</tr>\n				</tbody>\n			</table>\n		</td>\n		<td>{product_total_price_excl_vat}</td>\n	</tr>\n	<!-- {product_loop_end} -->\n	</tbody>\n</table>\n<p><strong class="discount_text"><br/></strong></p>\n<table style="width: 100%;" border="0" cellspacing="0" cellpadding="0">\n	<tbody>\n	<tr>\n		<td width="50%" valign="top">\n			<table border="0">\n				<tbody>\n				<tr>\n					<td>{update}</td>\n					<td>{empty_cart}</td>\n				</tr>\n				<tr>\n					<td class="cart_discount_form" colspan="2">{discount_form_lbl}{coupon_code_lbl}<br/>{discount_form}\n					</td>\n				</tr>\n				</tbody>\n			</table>\n			<br/></td>\n		<td width="50%" align="right" valign="top"><br/><br/>\n			<table class="cart_calculations" border="0" width="100%">\n				<tbody>\n				<tr class="tdborder">\n					<td><b>{product_subtotal_excl_vat_lbl}:</b></td>\n					<td width="100">{product_subtotal_excl_vat}</td>\n				</tr>\n				<!-- {if discount}-->\n				<tr class="tdborder">\n					<td>{discount_lbl}</td>\n					<td width="100">{discount}</td>\n				</tr>\n				<!-- {discount end if} -->\n				<tr>\n					<td><b>{shipping_with_vat_lbl}:</b></td>\n					<td width="100">{shipping_excl_vat}</td>\n				</tr>\n				<!-- {if vat} -->\n				<tr>\n					<td>{vat_lbl}</td>\n					<td width="100">{tax}</td>\n				</tr>\n				<!-- {vat end if} -->\n				<!-- {if payment_discount}-->\n				<tr>\n					<td>{payment_discount_lbl}</td>\n					<td width="100">{payment_order_discount}</td>\n				</tr>\n				<!-- {payment_discount end if}-->\n				<tr>\n					<td>\n						<div class="singleline"><strong>{total_lbl}:</strong></div>\n					</td>\n					<td width="100">\n						<div class="singleline">{total}</div>\n					</td>\n				</tr>\n				</tbody>\n			</table>\n\n			{checkout_button}<br/><br/> {shop_more}\n		</td>\n	</tr>\n	</tbody>\n</table>', '', '', 0, '', 0, '0000-00-00 00:00:00'),
(11, 'review', 'review', '<div>{product_loop_start}\n	<p><strong>{product_title}</strong></p>\n\n	<div>{review_loop_start}\n		<div id="reviews_wrapper">\n			<div id="reviews_rightside">\n				<div id="reviews_fullname">{fullname}</div>\n				<div id="reviews_title">{title}</div>\n				<div id="reviews_comment">{comment}</div>\n				<div id="reviews_date">{reviewdate}</div>\n			</div>\n			<div id="reviews_leftside">\n				<div id="reviews_stars">{stars}</div>\n			</div>\n		</div>\n		{review_loop_end}\n	</div>\n	{product_loop_end}\n</div>\n', '', '', 0, '', 0, '0000-00-00 00:00:00'),
(13, 'manufacturer_listings', 'manufacturer', '<div class="category_print">{print}</div>\n<div id="category_header">\n	<div class="category_order_by">{order_by}</div>\n</div>\n<div class="manufacturer_box_wrapper">{manufacturer_loop_start}\n	<div class="manufacturer_box_outside">\n		<div class="manufacturer_box_inside">\n			<div class="manufacturer_image">{manufacturer_image}</div>\n			<div class="manufacturer_title">\n				<h3>{manufacturer_name}</h3>\n			</div>\n			<div class="manufacturer_desc">{manufacturer_description}</div>\n			<div class="manufacturer_link"><a href="{manufacturer_link}">{manufacturer_link_lbl}</a></div>\n			<div class="manufacturer_product_link"><a href="{manufacturer_allproductslink}">{manufacturer_allproductslink_lbl}</a>\n			</div>\n		</div>\n	</div>\n	{manufacturer_loop_end}\n</div>\n', '', '', 0, '', 0, '0000-00-00 00:00:00'),
(14, 'manufacturer_products', 'manufacturer_products', '<div class="manufacturer_name">{manufacturer_name}</div>\n<div class="manufacturer_image">{manufacturer_image}</div>\n<div class="manufacturer_description">{manufacturer_description}</div>\n<div id="category_header">\n	<div class="category_order_by">{order_by}</div>\n</div>\n<div class="category_box_wrapper">\n	{product_loop_start}\n	<div class="category_box_outside">\n		<div class="category_box_inside">\n\n			<table border="0">\n				<tbody>\n				<tr>\n					<td colspan="2">\n						<div class="category_product_image">{product_thumb_image}</div>\n					</td>\n				</tr>\n				<tr>\n					<td colspan="2">\n						<div class="category_product_title">\n							<h3>{product_name}</h3>\n						</div>\n					</td>\n				</tr>\n				<tr>\n					<td>\n						<div class="category_product_price">{product_price}</div>\n					</td>\n					<td>\n						<div class="category_product_addtocart">{form_addtocart:add_to_cart1}</div>\n					</td>\n				</tr>\n				</tbody>\n			</table>\n		</div>\n	</div>\n	{product_loop_end}\n</div>\n<div class="category_pagination">{pagination}</div>\n', '', '', 1, '', 0, '0000-00-00 00:00:00'),
(15, 'order_list', 'order_list', '<table border="0" cellspacing="5" cellpadding="5" width="100%">\n	<tbody>\n	<tr>\n		<th>{order_id_lbl}</th>\n		<th>{product_name_lbl}</th>\n		<th>{total_price_lbl}</th>\n		<th>{order_date_lbl}</th>\n		<th>{order_status_lbl}</th>\n		<th>{order_detail_lbl}</th>\n	</tr>\n	<!--  {product_loop_start} -->\n	<tr>\n		<td style="background-color: #d7d7d4">{order_id}</td>\n		<td style="background-color: #d7d7d4">{order_products}</td>\n		<td style="background-color: #d7d7d4">{order_total}</td>\n		<td style="background-color: #d7d7d4">{order_date}</td>\n		<td style="background-color: #d7d7d4">{order_status}</td>\n		<td style="background-color: #d7d7d4">{order_detail_link}{reorder_link}</td>\n	</tr>\n	<!--  {product_loop_end} -->\n	</tbody>\n</table>\n{pagination}\n', '', '', 0, '', 0, '0000-00-00 00:00:00'),
(16, 'order_detail', 'order_detail', '<div class="product_print">{print}</div>\n<table style="width: 100%;" border="0" cellspacing="0" cellpadding="5">\n	<tbody>\n	<tr>\n		<td colspan="2">\n			<table style="width: 100%;" border="0" cellspacing="0" cellpadding="2">\n				<tbody>\n				<tr style="background-color: #cccccc">\n					<th align="left">{discount_type_lbl}</th>\n				</tr>\n				<tr>\n					<td>{discount_type}</td>\n				</tr>\n				<tr style="background-color: #cccccc;">\n					<th align="left">{order_information_lbl}</th>\n				</tr>\n				<tr>\n				</tr>\n				<tr>\n					<td>{order_id_lbl} : {order_id}</td>\n				</tr>\n				<tr>\n					<td>{order_number_lbl} : {order_number}</td>\n				</tr>\n				<tr>\n					<td>{order_date_lbl} : {order_date}</td>\n				</tr>\n				<tr>\n					<td>{order_status_lbl} : {order_status}</td>\n				</tr>\n				</tbody>\n			</table>\n		</td>\n	</tr>\n	<tr>\n		<td colspan="2">\n			<table style="width: 100%;" border="0" cellspacing="0" cellpadding="2">\n				<tbody>\n				<tr style="background-color: #cccccc;">\n					<th align="left">{billing_address_information_lbl}</th>\n				</tr>\n				<tr>\n				</tr>\n				<tr>\n					<td>{billing_address}</td>\n				</tr>\n				</tbody>\n			</table>\n		</td>\n	</tr>\n	<tr>\n		<td colspan="2">\n			<table style="width: 100%;" border="0" cellspacing="0" cellpadding="2">\n				<tbody>\n				<tr style="background-color: #cccccc;">\n					<th align="left">{shipping_address_information_lbl}</th>\n				</tr>\n				<tr>\n				</tr>\n				<tr>\n					<td>{shipping_address}</td>\n				</tr>\n				</tbody>\n			</table>\n		</td>\n	</tr>\n	<tr>\n		<td colspan="2">\n			<table style="width: 100%;" border="0" cellspacing="0" cellpadding="2">\n				<tbody>\n				<tr style="background-color: #cccccc;">\n					<th align="left">{order_detail_lbl}</th>\n				</tr>\n				<tr>\n				</tr>\n				<tr>\n					<td>\n						<table style="width: 100%;" border="0" cellspacing="2" cellpadding="2">\n							<tbody>\n							<tr>\n								<td>{copy_orderitem_lbl}</td>\n	